    private void controlaEmpresa(){
        if(cmbTipoEmpresa.getSelectedItem().equals("Matriz")){
            lblEmpresa.setText("<html>Empresa Matriz:</html>");
            lblEmpresa.setEnabled(false);
            cmbEmpresa.setEnabled(false);
            cmbEmpresa.setSelectedItem(null);
        } else {
            lblEmpresa.setText("<html>Empresa Matriz: <body> <span style=\"color: red;\">*</span></body></html>");
            lblEmpresa.setEnabled(true);
            cmbEmpresa.setEnabled(true);
                if(cmbEmpresa.getSelectedItem()==null){
                    carregarEmpresa(null);
                    cmbEmpresa.setSelectedItem(null);
                }
        }
        
    }
    
    private void carregarEmpresaAtual(){
        if(empresaCarregada.getIsHabilitado()==1)
            chkHabilitado.setSelected(true);
        else chkHabilitado.setSelected(false);
        txtRazaoSocial.setText(empresaCarregada.getRazaoSocial());
        SimpleDateFormat dateFormat = new SimpleDateFormat("dd/MM/yyyy");
        try {
            txtDataAbertura.setDate(dateFormat.parse(empresaCarregada.getDataAbertura()));
        } catch (ParseException ex) {
            Logger.getLogger(TelaEmpresa.class.getName()).log(Level.SEVERE, null, ex);
        }
        txtDataCadastro.setText(empresaCarregada.getDataCadastro());
        txtNomeFantasia.setText(empresaCarregada.getNomeFantasia());
        txtIM.setText(empresaCarregada.getIm());
        txtCNPJ.setText(empresaCarregada.getCnpj());
        txtIE.setText(empresaCarregada.getIe());
        txtEmail.setText(empresaCarregada.getEmail());
            if(empresaCarregada.getTelefoneFixo()!=null && !empresaCarregada.getTelefoneFixo().equals("(  )     -"))
                txtTelefoneFixo.setText(empresaCarregada.getTelefoneFixo());
            
            if(empresaCarregada.getTelefoneCelular()!=null && !empresaCarregada.getTelefoneCelular().equals("(  )      -"))
                txtTelefoneCelular.setText(empresaCarregada.getTelefoneCelular());
            
            if(empresaCarregada.getIsFilial()==1){
                cmbTipoEmpresa.setSelectedIndex(1);
                carregarEmpresa(empresaCarregada.getMatriz());
            } else cmbTipoEmpresa.setSelectedIndex(0);
        cmbEmpresa.setEnabled(false);
        txtLogradouro.setText(empresaCarregada.getLogradouro());
        txtNumero.setText(empresaCarregada.getNumero());
        txtCEP.setText(empresaCarregada.getCep());
        txtComplemento.setText(empresaCarregada.getComplemento());
        txtBairro.setText(empresaCarregada.getBairro());
        carregarEstado(empresaCarregada.getCidade().getEstado());
        carregarCidade(empresaCarregada.getCidade());
        txtObservacoes.setText(empresaCarregada.getObservacoes());
    }
